var config = require('./config.json');
var html_Page404 = function()
{
	var Page404 = '<div style="margin-top:50px;text-align:center;"><h1>Page not found<h1><h2><a href="/">Quay về Trang Chủ</a></h2></div>';
	return Page404;
}
var html_addDataToHeader = function()
{
	var strHeader = '<?xml version="1.0" encoding="UTF-8"?><!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.1//EN" "http://www.w3.org/TR/xhtml11/DTD/xhtml11.dtd">';
	strHeader += '<html xmlns="http://www.w3.org/1999/xhtml" xml:lang="en"><head><meta http-equiv="content-type" content="text/html; charset=utf-8" /><meta name="description" content="Simple Template #3 from simpletemplates.org" /><meta name="keywords" content="simple #3, template, simpletemplates.org" />';
	//script Header
	strHeader += '<link rel="stylesheet" type="text/css" href="' + config.FILE_STYLE_CSS + '" /><title>Simple Template #3 - simpletemplates.org</title></head>';
	//script Facebook
	strHeader += '<div id="fb-root"></div><script>(function(d, s, id) {  var js, fjs = d.getElementsByTagName(s)[0];  if (d.getElementById(id)) return;  js = d.createElement(s); js.id = id;  js.src = "//connect.facebook.net/en_US/sdk.js#xfbml=1&version=v2.0";  fjs.parentNode.insertBefore(js, fjs);}(document, "script", "facebook-jssdk"));</script>';
	//script body
	strHeader += '<body><div id="wrap"><div id="menu"><ul><li><a href="/">Home</a></li><li><a href="/dangchieu">Phim Đang Chiếu</a></li><li><a href="/sapchieu">Phim sắp chiếu</a></li><li><a href="/dachieu">Phim Đã Chiếu</a></li><li><a href="/chat">Rủ Rê Xem Phim</a></li><li><a href="/contact">Contact Us</a></li></ul></div>';
	return strHeader;
}

var html_addDataToFooter = function()
{
	var strFooter = '<div class="footer"><h2>© Copyright 2006-2014 Test</h2></div></div></body></html>';
	return strFooter;
}

var html_addGeneralDataToContent = function(items)
{
	if(items)
	{
		var resultStr =  "";
		for(var i=0;i<items.length;i++){
			for(var i=0;i<items.length;i++){
				resultStr += '<div id="content"><h2><a href="'+  config.PAGE_CURR + config.SYM_SEPARATE + items[i].id  +'">';
				resultStr += items[i].name + "</a>";
				resultStr += '</h2><img style="margin: 0 20px 20px 0; float: left; margin: 0 20px 0 0; width:180px; height: 250px;" src="';
				resultStr += config.PUBLIC_DIR + config.IMAGE_DATA_DIR + config.SYM_SEPARATE + items[i].image;
				resultStr += '" alt="image" /><p><b>Information:</b></p>';
				resultStr += items[i].summary;
				resultStr += '</p><div class="clear"> </div></div>';
			}
		}
		return resultStr;
	}
};

var html_addDetailDataToContent = function(item)
{
	if(item)
	{
		var resultRes = '<div id="content"><img style="margin: 0 20px 20px 0; float: left; margin: 0 20px 0 0; width:250px; height: 320px;" src="';	
		resultRes += config.PUBLIC_DIR + config.IMAGE_DATA_DIR + config.SYM_SEPARATE + item.image;
		resultRes += '" alt="image" /><h2><b>';
		resultRes += item.name;
		resultRes += '</b></h2><p><b>Director:</b>DIRECTOR</p><p><b>Cast:</b>Cast</p><p><b>Release Date:</b>';
		resultRes += item.date;
		resultRes +=  '</p><p><b>Running time:</b>Time</p><p><b>Category:</b>Cate</p><p><b>Information:</b>';
		resultRes += item.information;
		resultRes +=  '</p></div></p>';	
		
		resultRes += '<div class="fb-like" data-href="'+ config.HTTP_STRING + config.WEBSERVER_HOST + config.PAGE_NEW + config.SYM_SEPARATE + item.id;
		resultRes += '" data-layout="standard" data-action="like" data-show-faces="true" data-share="true"></div>';
		
		resultRes += '<div class="fb-comments" data-href="' + config.HTTP_STRING + config.WEBSERVER_HOST + config.PAGE_NEW + config.SYM_SEPARATE + item.id;
		resultRes +=  '" data-numposts="10" data-width="100%" data-colorscheme="light"></div>';			
		return resultRes;
	}
};

//##### Export for using in real App	
exports.html_addGeneralDataToContent = html_addGeneralDataToContent;
exports.html_addDetailDataToContent = html_addDetailDataToContent;
exports.html_addDataToHeader = html_addDataToHeader;
exports.html_addDataToFooter = html_addDataToFooter;
exports.html_Page404 = html_Page404;
